const result = $input.first().json;
const studies = result.studies || [];

if (studies.length === 0) return [];

const cureKeywords = ['cure', 'gene therapy', 'stem cell', 'beta cell', 'islet', 'immunotherapy', 'CAR T', 'regeneration', 'transplant', 'artificial pancreas', 'closed loop', 'glucose sensor'];

const filteredStudies = studies.filter(study => {
  const section = study.protocolSection || {};
  const id = section.identificationModule || {};
  const desc = section.descriptionModule || {};
  const conditions = section.conditionsModule?.conditions || [];
  const conditionText = conditions.join(' ').toLowerCase();
  const title = (id.briefTitle || '').toLowerCase();
  const summary = (desc.briefSummary || '').toLowerCase();
  const isT1D = conditionText.includes('type 1 diabetes') || conditionText.includes('t1d') || conditionText.includes('diabetes mellitus, type 1') || title.includes('type 1 diabetes');
  const allText = (title + ' ' + summary).toLowerCase();
  const isCureRelated = cureKeywords.some(keyword => allText.includes(keyword.toLowerCase()));
  return isT1D && isCureRelated;
});

return filteredStudies.map(study => {
  const section = study.protocolSection || {};
  const id = section.identificationModule || {};
  const desc = section.descriptionModule || {};
  const status = section.statusModule || {};
  const design = section.designModule || {};
  return {
    json: {
      ID: id.nctId || 'N/A',
      Title: id.briefTitle || 'No title',
      Content: desc.briefSummary || desc.detailedDescription || 'No summary',
      Phase: design.phases ? design.phases.join(', ') : 'Not specified',
      Status: status.overallStatus || 'Unknown',
      Date: status.startDateStruct?.date || 'Not specified',
      Source: 'ClinicalTrials.gov',
      Type: 'Clinical Trial',
      URL: `https://clinicaltrials.gov/study/${id.nctId}`
    }
  };
});

const result = $input.first().json;
const idList = result.esearchresult?.idlist || [];
if (idList.length === 0) return [];
return idList.map(pmid => ({ json: { pmid: pmid } }));

const items = [];
for (const item of $input.all()) {
  try {
    const xmlString = typeof item.json === 'string' ? item.json : JSON.stringify(item.json);
    const titleMatch = xmlString.match(/<ArticleTitle>([^<]+)<\/ArticleTitle>/);
    const abstractMatch = xmlString.match(/<AbstractText[^>]*>([\s\S]*?)<\/AbstractText>/);
    const pmidMatch = xmlString.match(/<PMID[^>]*>(\d+)<\/PMID>/);
    const yearMatch = xmlString.match(/<Year>(\d{4})<\/Year>/);
    const pmid = pmidMatch ? pmidMatch[1] : 'Unknown';
    const title = titleMatch ? titleMatch[1].trim().replace(/&#x[0-9a-fA-F]+;/g, '') : 'No title';
    let abstract = abstractMatch ? abstractMatch[1].trim() : 'No abstract';
    abstract = abstract.replace(/<[^>]+>/g, '').replace(/&#x[0-9a-fA-F]+;/g, '');
    const year = yearMatch ? yearMatch[1] : 'Unknown';
    if (title !== 'No title' && abstract !== 'No abstract' && abstract.length > 50) {
      items.push({
        json: {
          ID: pmid,
          Title: title,
          Content: abstract,
          Phase: 'N/A',
          Status: 'Published',
          Date: year,
          Source: 'PubMed',
          Type: 'Research Paper',
          URL: `https://pubmed.ncbi.nlm.nih.gov/${pmid}/`
        }
      });
    }
  } catch (error) {
    console.log('Error parsing PubMed item:', error);
  }
}
return items;

const items = [];
for (const item of $input.all()) {
  try {
    const originalData = {ID: item.json.ID, Type: item.json.Type, Title: item.json.Title, Content: item.json.Content, Phase: item.json.Phase, Status: item.json.Status, Date: item.json.Date, Source: item.json.Source, URL: item.json.URL};
    let responseContent = item.json.message?.content || '';
    responseContent = responseContent.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    const aiResponse = JSON.parse(responseContent);
    items.push({json: {PromiseLevel: aiResponse.promise_level || 0, Type: originalData.Type, ID: originalData.ID, Title: originalData.Title, KeyTakeaway: aiResponse.key_takeaway || 'N/A', ParentFriendly: aiResponse.parent_friendly || 'N/A', TechnicalSummary: aiResponse.technical_summary || 'N/A', Phase_Status: `${originalData.Phase} - ${originalData.Status}`, Date: originalData.Date, Source: originalData.Source, URL: originalData.URL}});
  } catch (error) {
    console.log('Error processing item:', error.message);
    items.push({json: {Error: `Processing failed: ${error.message}`, Title: item.json.Title || 'Unknown', ID: item.json.ID || 'Unknown'}});
  }
}
return items.sort((a, b) => (b.json.PromiseLevel || 0) - (a.json.PromiseLevel || 0));

const items = $input.all();

function getPromiseStyle(level) {
  if (level >= 4) return { color: '#10b981', emoji: 'üåü', label: 'High Promise' };
  if (level >= 3) return { color: '#f59e0b', emoji: '‚≠ê', label: 'Promising' };
  return { color: '#6b7280', emoji: 'üí°', label: 'Progress' };
}

const cardsHtml = items.map(item => {
  const data = item.json;
  const style = getPromiseStyle(data.PromiseLevel || 0);
  return '<div class="card" data-type="' + data.Type + '" data-promise="' + data.PromiseLevel + '" onclick="this.classList.toggle(\'expanded\')"><div class="promise-badge" style="background: ' + style.color + ';">' + style.emoji + ' ' + style.label + '</div><div class="card-type">' + data.Type + '</div><h2 class="card-title">' + data.Title + '</h2><div class="key-takeaway"><strong>Key Point:</strong> ' + data.KeyTakeaway + '</div><div class="parent-summary">' + data.ParentFriendly + '</div><div class="details"><div class="technical-summary"><strong>Technical Details:</strong><br><br>' + data.TechnicalSummary + '</div><div class="meta-info"><div class="meta-item"><strong>ID:</strong> ' + data.ID + '</div><div class="meta-item"><strong>Status:</strong> ' + data.Phase_Status + '</div><div class="meta-item"><strong>Date:</strong> ' + data.Date + '</div><div class="meta-item"><strong>Source:</strong> ' + data.Source + '</div></div><a href="' + data.URL + '" target="_blank" class="read-more">View Full Study ‚Üí</a></div><p class="expand-hint">Click to expand for more</p></div>';
}).join('');

