// Parse NEJM RSS feed for T1D research
const items = [];

// T1D and diabetes-related keywords
const t1dKeywords = [
  'type 1 diabetes', 't1d', 'diabetes mellitus', 'insulin', 'beta cell', 'islet',
  'autoimmune', 'pancreas', 'glucose', 'insulin resistance', 'diabetic',
  'hyperglycemia', 'hypoglycemia', 'glucagon', 'c-peptide', 'hba1c',
  'diabetic ketoacidosis', 'dka', 'microvascular', 'macrovascular',
  'diabetic retinopathy', 'diabetic nephropathy', 'diabetic neuropathy'
];

// Cure and treatment keywords
const cureKeywords = [
  'cure', 'treatment', 'therapy', 'trial', 'study', 'research', 'breakthrough',
  'discovery', 'innovation', 'clinical', 'immunotherapy', 'stem cell',
  'gene therapy', 'artificial pancreas', 'closed loop', 'prevention', 'delay',
  'regeneration', 'transplant', 'encapsulation', 'immune tolerance'
];

try {
  const input = $input.first();
  if (input && input.json) {
    const xmlString = typeof input.json === 'string' ? input.json : JSON.stringify(input.json);

    // Parse RSS XML for NEJM articles
    const itemMatches = xmlString.match(/<item[^>]*>([\s\S]*?)<\/item>/g);
    if (itemMatches && itemMatches.length > 0) {
      for (const itemXml of itemMatches) {
        try {
          // Extract title
          const titleMatch = itemXml.match(/<title><!\[CDATA\[([^\]]+)\]\]><\/title>/) || itemXml.match(/<title>([^<]+)<\/title>/);
          const descMatch = itemXml.match(/<description><!\[CDATA\[([^\]]+)\]\]><\/description>/) || itemXml.match(/<description>([^<]+)<\/description>/);
          const linkMatch = itemXml.match(/<link>([^<]+)<\/link>/);
          const dateMatch = itemXml.match(/<pubDate>([^<]+)<\/pubDate>/);

          const title = titleMatch ? titleMatch[1].replace(/<[^>]*>/g, '').trim() : 'NEJM Article';
          const description = descMatch ? descMatch[1].replace(/<[^>]*>/g, '').trim() : 'NEJM research article';
          const link = linkMatch ? linkMatch[1] : 'https://www.nejm.org/';
          const pubDate = dateMatch ? dateMatch[1] : new Date().toISOString();

          // Check if it's T1D or diabetes-related
          const allText = (title + ' ' + description).toLowerCase();
          const isT1dRelated = t1dKeywords.some(keyword => allText.includes(keyword));
          const isCureRelated = cureKeywords.some(keyword => allText.includes(keyword));

          if (isT1dRelated || isCureRelated) {
            // Determine promise level based on content
            let promiseLevel = 3; // Default for general medical research
            if (allText.includes('cure') || allText.includes('breakthrough') || allText.includes('clinical trial')) {
              promiseLevel = 5;
            } else if (allText.includes('treatment') || allText.includes('therapy') || allText.includes('study')) {
              promiseLevel = 4;
            }

            items.push({
              json: {
                ID: `nejm-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
                Title: title,
                Content: description,
                Phase: 'N/A',
                Status: 'Published',
                Date: pubDate,
                Source: 'NEJM Research RSS',
                Type: 'Research Article',
                URL: link,
                PromiseLevel: promiseLevel
              }
            });
          }
        } catch (error) {
          console.log('Error parsing NEJM item:', error);
        }
      }
    }

    // If no T1D-specific articles found, create a general NEJM update
    if (items.length === 0) {
      items.push({
        json: {
          ID: `nejm-general-${Date.now()}`,
          Title: 'NEJM: Latest Medical Research',
          Content: 'Recent research articles from the New England Journal of Medicine covering various medical conditions and treatments.',
          Phase: 'N/A',
          Status: 'Published',
          Date: new Date().getFullYear().toString(),
          Source: 'NEJM Research RSS',
          Type: 'Research Update',
          URL: 'https://www.nejm.org/',
          PromiseLevel: 3
        }
      });
    }
  }
} catch (error) {
  console.log('Error in NEJM parsing:', error);
  // Fallback content
  items.push({
    json: {
      ID: `nejm-fallback-${Date.now()}`,
      Title: 'NEJM: Medical Research Updates',
      Content: 'Latest research from the New England Journal of Medicine.',
      Phase: 'N/A',
      Status: 'Published',
      Date: new Date().getFullYear().toString(),
      Source: 'NEJM Research RSS',
      Type: 'Research Update',
      URL: 'https://www.nejm.org/',
      PromiseLevel: 3
    }
  });
}

return items;
