const result = $input.first().json;
const studies = result.studies || [];

if (studies.length === 0) return [];

// Enhanced cure keywords - more comprehensive
const cureKeywords = [
  'cure', 'gene therapy', 'stem cell', 'beta cell', 'islet', 'immunotherapy', 
  'CAR T', 'regeneration', 'transplant', 'artificial pancreas', 'closed loop', 
  'glucose sensor', 'insulin pump', 'continuous glucose monitoring', 'CGM',
  'beta cell replacement', 'islet transplantation', 'encapsulation',
  'immune tolerance', 'autoimmune suppression', 'prevention', 'delay onset',
  'teplizumab', 'verapamil', 'metformin', 'GLP-1', 'SGLT2', 'DPP-4',
  'encapsulated', 'macroencapsulation', 'microencapsulation', 'xenotransplant'
];

const filteredStudies = studies.filter(study => {
  const section = study.protocolSection || {};
  const id = section.identificationModule || {};
  const desc = section.descriptionModule || {};
  const conditions = section.conditionsModule?.conditions || [];
  const design = section.designModule || {};

  const conditionText = conditions.join(' ').toLowerCase();
  const title = (id.briefTitle || '').toLowerCase();
  const summary = (desc.briefSummary || '').toLowerCase();
  const detailedDesc = (desc.detailedDescription || '').toLowerCase();

  // Check for T1D
  const isT1D = conditionText.includes('type 1 diabetes') || 
                conditionText.includes('t1d') || 
                conditionText.includes('diabetes mellitus, type 1') || 
                title.includes('type 1 diabetes');

  // Check for cure-related content
  const allText = (title + ' ' + summary + ' ' + detailedDesc).toLowerCase();
  const isCureRelated = cureKeywords.some(keyword => 
    allText.includes(keyword.toLowerCase())
  );

  // Prioritize Phase 1 and 2 trials
  const phases = design.phases || [];
  const isEarlyPhase = phases.some(phase => 
    phase.toLowerCase().includes('phase 1') || 
    phase.toLowerCase().includes('phase 2')
  );

  return isT1D && (isCureRelated || isEarlyPhase);
});

return filteredStudies.map(study => {
  const section = study.protocolSection || {};
  const id = section.identificationModule || {};
  const desc = section.descriptionModule || {};
  const status = section.statusModule || {};
  const design = section.designModule || {};

  // Calculate promise level based on phase and content
  let promiseLevel = 1;
  const phases = design.phases || [];
  if (phases.some(p => p.toLowerCase().includes('phase 1'))) promiseLevel = 4;
  else if (phases.some(p => p.toLowerCase().includes('phase 2'))) promiseLevel = 3;
  else if (phases.some(p => p.toLowerCase().includes('phase 3'))) promiseLevel = 2;

  return {
    json: {
      ID: id.nctId || 'N/A',
      Title: id.briefTitle || 'No title',
      Content: desc.briefSummary || desc.detailedDescription || 'No summary',
      Phase: design.phases ? design.phases.join(', ') : 'Not specified',
      Status: status.overallStatus || 'Unknown',
      Date: status.startDateStruct?.date || 'Not specified',
      Source: 'ClinicalTrials.gov',
      Type: 'Clinical Trial',
      URL: `https://clinicaltrials.gov/study/${id.nctId}`,
      PromiseLevel: promiseLevel
    }
  };
});

const result = $input.first().json;
const idList = result.esearchresult?.idlist || [];
if (idList.length === 0) return [];
return idList.map(pmid => ({ json: { pmid: pmid } }));

const items = [];
for (const item of $input.all()) {
  try {
    const xmlString = typeof item.json === 'string' ? item.json : JSON.stringify(item.json);
    const titleMatch = xmlString.match(/<ArticleTitle>([^<]+)<\/ArticleTitle>/);
    const abstractMatch = xmlString.match(/<AbstractText[^>]*>([\s\S]*?)<\/AbstractText>/);
    const pmidMatch = xmlString.match(/<PMID[^>]*>(\d+)<\/PMID>/);
    const yearMatch = xmlString.match(/<Year>(\d{4})<\/Year>/);
    const journalMatch = xmlString.match(/<JournalTitle>([^<]+)<\/JournalTitle>/);

    const pmid = pmidMatch ? pmidMatch[1] : 'Unknown';
    const title = titleMatch ? titleMatch[1].trim().replace(/&#x[0-9a-fA-F]+;/g, '') : 'No title';
    let abstract = abstractMatch ? abstractMatch[1].trim() : 'No abstract';
    abstract = abstract.replace(/<[^>]+>/g, '').replace(/&#x[0-9a-fA-F]+;/g, '');
    const year = yearMatch ? yearMatch[1] : 'Unknown';
    const journal = journalMatch ? journalMatch[1] : 'Unknown';

    // Determine promise level based on journal and content
    let promiseLevel = 2;
    const highImpactJournals = ['Nature', 'Science', 'Cell', 'New England Journal of Medicine', 'Lancet', 'Nature Medicine', 'Cell Stem Cell'];
    if (highImpactJournals.some(j => journal.includes(j))) {
      promiseLevel = 4;
    }

    const allText = (title + ' ' + abstract).toLowerCase();
    if (allText.includes('cure') || allText.includes('breakthrough') || allText.includes('clinical trial')) {
      promiseLevel = Math.min(promiseLevel + 1, 5);
    }

    items.push({
      json: {
        ID: pmid,
        Title: title,
        Content: abstract,
        Phase: 'N/A',
        Status: 'Published',
        Date: year,
        Source: 'PubMed',
        Type: 'Research Article',
        URL: `https://pubmed.ncbi.nlm.nih.gov/${pmid}/`,
        PromiseLevel: promiseLevel
      }
    });
  } catch (error) {
    console.log('Error parsing PubMed item:', error);
  }
}
return items;

// Parse Nature Medicine RDF feed for T1D research
const items = [];

// T1D and diabetes-related keywords
const t1dKeywords = [
  'type 1 diabetes', 't1d', 'diabetes mellitus', 'insulin', 'beta cell', 'islet',
  'autoimmune', 'pancreas', 'glucose', 'insulin resistance', 'diabetic',
  'hyperglycemia', 'hypoglycemia', 'glucagon', 'c-peptide', 'hba1c'
];

// Cure and treatment keywords
const cureKeywords = [
  'cure', 'treatment', 'therapy', 'trial', 'study', 'research', 'breakthrough',
  'discovery', 'innovation', 'clinical', 'immunotherapy', 'stem cell',
  'gene therapy', 'artificial pancreas', 'closed loop', 'prevention', 'delay',
  'regeneration', 'transplant', 'encapsulation', 'immune tolerance'
];

try {
  const input = $input.first();
  if (input && input.json) {
    const xmlString = typeof input.json === 'string' ? input.json : JSON.stringify(input.json);

    // Parse RDF XML for Nature Medicine articles
    const itemMatches = xmlString.match(/<item rdf:about="[^"]*">([\s\S]*?)<\/item>/g);
    if (itemMatches && itemMatches.length > 0) {
      for (const itemXml of itemMatches) {
        try {
          // Extract title from CDATA
          const titleMatch = itemXml.match(/<title><!\[CDATA\[([^\]]+)\]\]><\/title>/);
          const contentMatch = itemXml.match(/<content:encoded><!\[CDATA\[([^\]]+)\]\]><\/content:encoded>/);
          const linkMatch = itemXml.match(/<link>([^<]+)<\/link>/);
          const dateMatch = itemXml.match(/<dc:date>([^<]+)<\/dc:date>/);

          const title = titleMatch ? titleMatch[1].replace(/<[^>]*>/g, '').trim() : 'Nature Medicine Article';
          const content = contentMatch ? contentMatch[1].replace(/<[^>]*>/g, '').trim() : 'Nature Medicine research article';
          const link = linkMatch ? linkMatch[1] : 'https://www.nature.com/nm/';
          const pubDate = dateMatch ? dateMatch[1] : new Date().toISOString();

          // Check if it's T1D or diabetes-related
          const allText = (title + ' ' + content).toLowerCase();
          const isT1dRelated = t1dKeywords.some(keyword => allText.includes(keyword));
          const isCureRelated = cureKeywords.some(keyword => allText.includes(keyword));

          if (isT1dRelated || isCureRelated) {
            items.push({
              json: {
                ID: `nature-med-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
                Title: title,
                Content: content,
                Phase: 'N/A',
                Status: 'Published',
                Date: pubDate,
                Source: 'Nature Medicine RSS',
                Type: 'Research Article',
                URL: link,
                PromiseLevel: 4 // High-impact journal
              }
            });
          }
        } catch (error) {
          console.log('Error parsing Nature Medicine item:', error);
        }
      }
    }

    // If no T1D-specific articles found, create a general Nature Medicine update
    if (items.length === 0) {
      items.push({
        json: {
          ID: `nature-med-general-${Date.now()}`,
          Title: 'Nature Medicine: Latest Medical Research',
          Content: 'Recent research articles from Nature Medicine covering various medical conditions and treatments.',
          Phase: 'N/A',
          Status: 'Published',
          Date: new Date().getFullYear().toString(),
          Source: 'Nature Medicine RSS',
          Type: 'Research Update',
          URL: 'https://www.nature.com/nm/',
          PromiseLevel: 4
        }
      });
    }
  }
} catch (error) {
  console.log('Error in Nature Medicine parsing:', error);
  // Fallback content
  items.push({
    json: {
      ID: `nature-med-fallback-${Date.now()}`,
      Title: 'Nature Medicine: Medical Research Updates',
      Content: 'Latest research from Nature Medicine.',
      Phase: 'N/A',
      Status: 'Published',
      Date: new Date().getFullYear().toString(),
      Source: 'Nature Medicine RSS',
      Type: 'Research Update',
      URL: 'https://www.nature.com/nm/',
      PromiseLevel: 4
    }
  });
}

return items;

